import React, { Component } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import '../stylesheets/SearchResult.css';

/*
* 
*/
class SearchResult extends Component {

	constructor(props) {
		super(props);
		this.state = {
			is_gif: true,
		}
	}

	searchResultToExpandable() {
		return this.props.search_content.map((pic)=>{
			return (<ExpandableGif key={pic.id} active_title={this.props.active_title} is_gif={this.state.is_gif} height_is_fixed={false} handleSubmit={this.props.handleSubmit} handleShareButton={this.props.handleShareButton} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
		})
	}

	setGif(boolean) {
		this.setState({
			is_gif: boolean,
		});
	}

	render() {
		if (this.props.search_content.length === 0) {
			return null; 
		}

		const style = {
			transform: this.state.is_gif ? 'translateX(0)': 'translateX(52px)',
			width: this.state.is_gif ? '51px' : '86px',
		}; 

		return (
		<div className="search-result">
			<h1 className='GIF-TITLE flex relative'>
				<div className="trending-btn">Related</div>
			</h1>
			<h1 className="GIF-TITLE flex relative">
				<div className="trending-btn" onClick={()=>{this.setGif(true);this.props.handleSubmit(this.props.active_title, true)}}>Gifs</div>
				<div className="favorite-btn" onClick={()=>{this.setGif(false);this.props.handleSubmit(this.props.active_title, false)}}>Stickers</div>
				<div className="animated-ellipse" style={style}></div>
			</h1>
			<div className="search-result-container">{this.searchResultToExpandable()}</div>
		</div>
		);
	}
}

export default SearchResult;
