import React, { Component } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import '../stylesheets/GifRandom.css';

/*
* 
*/
class GifRandom extends Component {

	constructor() {
		super();
		this.state = {
			randoms: [],
			rating: 'g',
			maxRandom: 10,
		}
	}

	/*
	*  Search Endpoint with random string and rating 
	*/
	componentWillMount() {
		const api_key = 'uAUOhgJClefqTwSNK9DZKnZv7r8DHZbw';
		var randomWords = require('random-words');
		const random = randomWords();
		console.log(random);
		const path = 'https://api.giphy.com/v1/gifs/search?api_key=' + api_key + '&q=' + random +'&rating=' + this.state.rating;
	    fetch(path, {mode: 'cors'})
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				randoms: data.data,
			});
		})
	}

	randomsToExpandable(){
		return this.state.randoms.map((pic)=>{
			return (<ExpandableGif key={pic.id} handleShareButton={this.props.handleShareButton} horizontal_trending={this.props.horizontal_trending} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
		});
	}

	render() {
		if (this.props.active_gif !== null) {
			return null;
		}
		
		return (
			<div>
			{this.randomsToExpandable()}
			</div>
		);
	}
}

export default GifRandom;
