import React, { Component } from 'react';
import '../stylesheets/SearchBar.css';

/*
* Search Endpoint
* https://developers.giphy.com/docs/ 
*/
class SearchBar extends Component {

	constructor(props) {
		super(props);
		this.placeholders = [
			"Search all GIFs and Stickers",
			"@username + tag to search within verified channel",
			"Search all GIFs and Stickers"
		];
		this.state = {
			animate: true,
			value: '',
		}
		this.handleInputChange = this.handleInputChange.bind(this);
	}

	renderPlaceholder() {
		if (this.state.animate) {
			return (
				<div>
					<div className="animate-container">
						{ this.placeholders.map((p, index)=>{ return <p key={index}>{p}</p>; })}
					</div>
				</div>
			);
		}
	}

	/*
	* Only animate placeholder if value null
	*/
	handleInputChange(event) {
		this.setState({
			animate: event.target.value.trim() === '',
			value: event.target.value,
		});
	}

	render() {
		const img = require('../icons/search.svg');
		return (
			<form className="search-bar" onSubmit={(event)=>{this.props.handleSubmitAndActive(event, this.state.value, true);}}>
				{this.renderPlaceholder()}
				<input type="text" value={this.state.value} onChange={this.handleInputChange}
				autoCapitalize="off" autoCorrect="off" autoComplete="off"/>
				<div className="search-btn">
					<input type="submit" value=''/>
					<img src={img} alt='search-icon'/>
				</div>
			</form>
		);
	}
}

export default SearchBar;
