import React, { Component } from 'react';
import Trending from '../components/Trending'; 
import GifExpanded from '../components/GifExpanded';
import '../stylesheets/MainContent.css';

/*
* Includes:
* 	- Trending 
*	- Gif Expanded
*/
class MainContent extends Component {

	constructor(props) {
		super(props);
		this.state = {
			info_is_off: false,
			favorites: [],
			share_is_off: true,
		}
		this.handleInfoButton = this.handleInfoButton.bind(this);
		this.handleFavoriteButton = this.handleFavoriteButton.bind(this);
		this.handleShareButton = this.handleShareButton.bind(this);
	}

	handleShareButton(boolean) {
		this.setState({
			share_is_off: boolean,
		});
	}

	handleInfoButton(boolean) {
		this.setState({
			info_is_off: boolean,
		});
	}

	handleFavoriteButton(pic_object) {
		var copy = this.state.favorites.slice();
		var ids = this.state.favorites.map((pic)=>{
			return pic.id;
		})
		if (ids.indexOf(pic_object.id) > -1) {
			copy.splice(ids.indexOf(pic_object.id), 1);
			this.setState({
				favorites: copy,
			});
		} else {
			this.setState({
				favorites: [pic_object,...this.state.favorites],
			});
		}
	}

	render() {
		const style = {
			display: this.props.active_gif === null ? 'block' : 'flex',
		}
		return (
		  <div className="main-content" style={style}>
		  	<GifExpanded share_is_off={this.state.share_is_off} handleShareButton={this.handleShareButton} favorites={this.state.favorites} handleFavoriteButton={this.handleFavoriteButton} pic={this.props.active_gif} info_is_off={this.state.info_is_off} handleInfoButton={this.handleInfoButton} dimensions={this.props.dimensions} sad_gif={this.props.sad_gif}/>
		  	<Trending favorites={this.state.favorites} handleShareButton={this.handleShareButton}  horizontal_trending={this.props.horizontal_trending} handleInfoButton={this.handleInfoButton} handleActiveGif={this.props.handleActiveGif}/>
		  </div>
		);
	}
}

export default MainContent;
