import React, { Component } from 'react';
import '../stylesheets/GifRandom.css';

/*
* 
*/
class GifRandom extends Component {

	constructor() {
		super();
		this.state = {
			randoms: [],
		}
	}

	/*
	*  Random Gifs according to Rating 
	*/
	handleRandom(rating) {
		const path = 'https://api.giphy.com/v1/gifs/random?api_key=20RpuHkJdS4eOEQg5OOBjQNSqLAnzgmP&q=' + rating;
	    fetch(path)
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				horizontal_trending: false,
				main_content: data.data,
			});
		})
	}

	render() {
		if (this.props.active_gif) {
			return null;
		}

		return (
		  <div className="App">
		  	RANDOM
		  </div>
		);
	}
}

export default GifRandom;
