import React, { Component } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import '../stylesheets/GifRandom.css';

/*
* 
*/
class GifRandom extends Component {

	constructor() {
		super();
		this.state = {
			randoms: [],
			rating: 'g',
			maxRandom: 10,
		}
	}

	/*
	*  Random Gifs according to Rating 
	*/
	componentWillMount() {
		const api_key = 'ewnfHypctYT5Bn4nXkQpC9ebtdX9dDBG';
		const path = 'https://api.giphy.com/v1/gifs/random?api_key=' + api_key + '&rating=' + this.state.rating;
	    let i = 0;
	    let randoms = [];
	    // for (i = 0;i < this.state.maxRandom;i++) {
	    	fetch(path)
			.then(results => {
				return results.json();
			}).then(data=> {
				randoms.push(data.data);
			});	
		// }
		console.log(randoms);
		// this.setState({
		// 	randoms: randoms,
		// });
	}

	randomsToExpandable(){
		return this.state.randoms.map((pic)=>{
			return (<ExpandableGif key={pic.id} handleShareButton={this.props.handleShareButton} horizontal_trending={this.props.horizontal_trending} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
		});
	}

	render() {
		if (this.props.active_gif !== null) {
			return null;
		}
		
		return (
			<div>
			{this.randomsToExpandable()}
			</div>
		);
	}
}

export default GifRandom;
