import React, { Component } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import '../stylesheets/Trending.css';
/*
* https://kenwheeler.github.io/slick/
*/
import Slider from 'react-slick'; 
import 'slick-carousel/slick/slick-theme.css';
import 'slick-carousel/slick/slick.css';

/*
* Trending Endpoint
* https://developers.giphy.com/docs/
*/
class Trending extends Component {

	constructor(props) {
		super(props);
		this.state = {
			pictures: [],
			is_trending_bar: true, 
			is_gif: true,
		}
	}

	setTrending(boolean) {
		this.setState({
			is_trending_bar: boolean,
		});
	}

	setGif(boolean) {
		this.setState({
			is_gif: boolean,
		});
	}

	componentWillMount() {
		this.setPictures(true);
	}

	setPictures(is_gif) {
		let path = 'https://api.giphy.com/v1/'
		if (is_gif) {
			path += 'gifs'
		} else {
			path += 'stickers'
		}
		path += '/trending?api_key=' + this.props.api_key;	
		console.log(path)

		fetch(path)
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({pictures: data.data});
		})
	}

	/*
	* Logic for horizontal and vertical trending  
	*/
	picturesToExpandable() {
		// if (this.state.pictures.length === 0) {
		// 	return (
		// 		<div className="favorite-error">
		// 			<div className="line-2">
		// 				<div>{"Click"}</div>
		// 				<div style={{'margin': '0 10px'}}>{this.renderFavoriteButton()}</div>
		// 				<div>{"to favorite a GIF"}</div>
		// 			</div>
		// 		</div>
		// 	);
		// }
		return this.state.pictures.map((pic)=>{
			return (<ExpandableGif key={pic.id} height_is_fixed={true} handleShareButton={this.props.handleShareButton} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
		})
	}

	// renderFavoriteButton(){
	// 	return(
	// 		<svg className="favorite" version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px"
	// 			 width="49.655px" height="49.654px" viewBox="0 0 49.655 49.654" >
	// 		<g>
	// 			<g>
	// 				<path className="star" style={{'fill': 'var(--title-color)'}} d="M24.827,0C11.138,0,0,11.137,0,24.826c0,13.688,11.137,24.828,24.826,24.828c13.688,0,24.827-11.14,24.827-24.828
	// 					C49.653,11.137,38.516,0,24.827,0z M38.543,23.011L38.543,23.011l-7.188,5.201l2.769,8.46c0.044,0.133,0.067,0.273,0.067,0.417
	// 					c0,0.739-0.6,1.339-1.339,1.339c-0.293,0-0.562-0.094-0.782-0.252l-7.244-5.24l-7.243,5.24c-0.22,0.158-0.49,0.252-0.783,0.252
	// 					c-0.74,0-1.339-0.599-1.339-1.339c0-0.146,0.024-0.284,0.068-0.417l2.769-8.46l-7.187-5.2v-0.001
	// 					c-0.336-0.242-0.554-0.637-0.554-1.083c0-0.739,0.598-1.337,1.338-1.337h8.897l2.755-8.421c0.168-0.547,0.677-0.945,1.28-0.945
	// 					c0.603,0,1.112,0.398,1.28,0.945l2.754,8.421h8.896c0.739,0,1.338,0.598,1.338,1.337C39.096,22.374,38.878,22.769,38.543,23.011z"
	// 					/>
	// 			</g>
	// 		</g>
	// 		</svg>
	// 	);
	// }

	// favoritesToExpandable() {
	// 	if (this.props.favorites.length === 0) {
	// 		return (
	// 			<div className="favorite-error">
	// 				<div className="line-2">
	// 					<div>{"Click"}</div>
	// 					<div style={{'margin': '0 10px'}}>{this.renderFavoriteButton()}</div>
	// 					<div>{"to favorite a GIF"}</div>
	// 				</div>
	// 			</div>
	// 		);
	// 	}
	// 	return this.props.favorites.map((pic)=>{
	// 		return (<ExpandableGif key={pic.id} handleShareButton={this.props.handleShareButton} horizontal_trending={this.props.horizontal_trending} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
	// 	});
	// }

	render() {
		/*
		* Variable Width Slick Carousel 
		*/
		var settings = {
		  dots: false,
		  infinite: false,
		  speed: 300,
		  slidesToShow: 1,
		  centerMode: false,
		  variableWidth: true
		};

		console.log('this.state.gif', this.state.is_gif);

		let slider = (<Slider className="gif-list" {...settings}>
						{this.state.is_trending_bar ? this.picturesToExpandable() : this.favoritesToExpandable()}
					</Slider>);
		let className = "trending";
		// if (!this.props.horizontal_trending) {
		// 	className += ' trending-vertical';
		// 	slider = (<div className="vertical">
		// 				{this.state.is_trending_bar ? this.picturesToExpandable() : this.favoritesToExpandable()}
		// 			</div>);
		// }
		const style_2 = {
			transform: this.state.is_gif ? 'translateX(0)': 'translateX(52px)',
			width: this.state.is_gif ? '51px' : '86px',
		}; 

		return (
			<div className={className}>
				<div className="title-container">
					<h1 className='GIF-TITLE flex relative'>
						<div className="trending-btn">Trending</div>
					</h1>
					<h1 className="GIF-TITLE flex relative">
						<div className="trending-btn" onClick={()=>{this.setGif(true);this.setPictures(true)}}>Gifs</div>
						<div className="favorite-btn" onClick={()=>{this.setGif(false);this.setPictures(false)}}>Stickers</div>
						<div className="animated-ellipse" style={style_2}></div>
					</h1>
				</div>
				{slider}
			</div>
		);
	}
}

export default Trending;
