import React, { Component } from 'react';
import SearchBar from '../components/SearchBar'; 
import MainContent from '../components/MainContent';
import Logo from '../components/Logo';
import '../stylesheets/Giphy.css';

/*
* Main Page of Giphy App 
*/
class Giphy extends Component {

	constructor() {
		super();
		this.state = {
			horizontal_trending: true,
			active_gif: null,  
			main_content: [],
			api_key: 'ewnfHypctYT5Bn4nXkQpC9ebtdX9dDBG',
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.setMainState = this.setMainState.bind(this);
		this.handleActiveGif = this.handleActiveGif.bind(this);
	}

	/*
	* Handle Submit for Search 
	*/
	handleSubmit(event) {
		const api_key = this.state.api_key;
		const path = 'https://api.giphy.com/v1/gifs/search?api_key=' + api_key + '&q=';
	    event.preventDefault();
	    fetch(path + event.target.value)
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				horizontal_trending: false,
				main_content: data.data,
			});
		})
	}

	/*
	* Sets active_gif in Giphy to a GIF object 
	*/	
	handleActiveGif(pic) {
		this.setState({
			active_gif: pic,
			horizontal_trending: false,
		});
	}

	/*
	* Sets layout and content to Main-Page state 
	*/
	setMainState() {
		this.setState({
			horizontal_trending: true,
			main_content: [],
			active_gif: null,
		});
	}

	render() {
		return (
			<div className="giphy">
				<Logo setMainState={this.setMainState}/>
		    	<SearchBar handleSubmit={this.handleSubmit}/>
		    	<MainContent api_key={this.state.api_key} sad_gif={this.state.sad_gif} dimensions={this.props.dimensions} main_content={this.state.main_content} handleActiveGif={this.handleActiveGif} active_gif={this.state.active_gif} horizontal_trending={this.state.horizontal_trending}/>
			</div>
		);
	}
}

export default Giphy;
