import React, { Component } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import '../stylesheets/GifRandom.css';

/*
* 
*/
class GifRandom extends Component {

	constructor() {
		super();
		this.state = {
			randoms: [],
			rating: 'g',
			is_gif: true,
			random_word: this.getRandom(),
		}
	}

	getRandom() {
		var randomWords = require('random-words');
		return randomWords();
	}

	/*
	*  Search Endpoint with random string and rating 
	*/
	componentWillMount() {
		/*
		* Selects a random word 
		*/
		console.log('random word: ', this.state.random_word);
		console.log('this.state.rating: ', this.state.rating);
		const api_key = 'uAUOhgJClefqTwSNK9DZKnZv7r8DHZbw';
		const path = 'https://api.giphy.com/v1/gifs/search?api_key=' + api_key + '&q=' + this.state.random_word +'&rating=' + this.state.rating;
	    fetch(path, {mode: 'cors'})
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				randoms: data.data,
			});
		})
	}

	randomsToExpandable(){
		return this.state.randoms.map((pic)=>{
			return (<ExpandableGif key={pic.id} handleShareButton={this.props.handleShareButton} horizontal_trending={false} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
		});
	}

	setGif(boolean) {
		this.setState({
			is_gif: boolean,
		});
	}

	render() {
		if (this.props.active_gif !== null) {
			return null;
		}

		const style = {
			transform: this.state.is_gif ? 'translateX(0)': 'translateX(52px)',
			width: this.state.is_gif ? '51px' : '86px',
		};
		
		return (
			<div>
				<h1 className="GIF-TITLE flex relative">
					<div className="trending-btn" onClick={()=>{this.setGif(true)}}>Gifs</div>
					<div className="favorite-btn" onClick={()=>{this.setGif(false)}}>Stickers</div>
					<div className="animated-ellipse" style={style}></div>
					<div className="random-word">{'Word: ' + this.state.random_word}</div>
				</h1>
				<div className="random-container">{this.randomsToExpandable()}</div>
			</div>
		);
	}
}

export default GifRandom;
