import React, { Component } from 'react';
/*
* Used in Trending
* onClick - Displays Gif Expanded  
*/
class ExpandableGif extends Component {

	titleToSearchable(){
		console.log('title');
		let title = this.props.pic.title.toLowerCase().trim();
		if (title === '') {
			console.log('slug')
			let slug = this.props.pic.slug.toLowerCase().trim().split('-');
			slug = slug.filter(function(str){return !(/\d/.test(str))})
			if (slug.length === 1) {
				console.log('username')
				return this.props.pic.username;
			}
			return slug;
		}
		let title_arr = title.split(" "); 
		return title_arr.slice(0, title_arr.indexOf('gif')).join(' ');
	}

	render() {
		const {title} = this.props.pic;
		/*
		* Images displayed according to Trending's Direction:
		* 	- Horizontal -- FIXED HEIGHT
		*	- Vertical   -- FIXED WIDTH
		*/
		const img = this.props.height_is_fixed ? this.props.pic.images.fixed_height : this.props.pic.images.fixed_width;
		const {url} = img;
		const {height} = img;
		const {width} = img; 
		console.log(this.titleToSearchable());
		/*
		* Weight scales IMG 
		* Default Fixed Height looks too large, scale it down 
		*/
		const WEIGHT = this.props.height_is_fixed ? .75 : 1;

		const style = {
			height: height*WEIGHT + 'px',
			width: width*WEIGHT + 'px',
			margin: this.props.height_is_fixed ? '6px 6px 6px 0px' : '0px 6px 6px 6px',
		}

		return (
			<img alt={title} src={url} style={style} onClick={()=>{
				this.props.handleActiveGif(this.props.pic);
				this.props.handleInfoButton(false);
				this.props.handleShareButton(false);
				window.scroll({top: 0, left: 0, behavior: 'smooth' });
			}} />
		);
	}
}

export default ExpandableGif;
