import React, { Component, Fragment } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import BottomScrollListener from 'react-bottom-scroll-listener';
import '../stylesheets/SearchResult.css';

/*
* 
*/
class SearchResult extends Component {

	searchResultToExpandable() {
		let result = this.props.search_content.map((pic)=>{
			return (<ExpandableGif key={pic.id} titleToSearchable={this.props.titleToSearchable} active_title={this.props.active_title} is_gif={this.state.is_gif} height_is_fixed={false} handleSubmit={this.props.handleSubmit} handleShareButton={this.props.handleShareButton} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
		});
		// console.log('result', result)
		if (result.length === 0) {
			return (
				<h1 className='GIF-TITLE'>
				{'No Content Found :\'('}
				</h1>
			);
		} else {
			return result;
		}
	}

	callback() { 
		alert('callback!!!!')
		console.log('callback')
		// if (this.state.active_gif !== null) {
		// 	console.log('implement later')
		// } else {
			this.handleSubmit(this.state.active_title, this.props.is_gif, this.props.search_content.length)
		// }
	}

	render() {
		if (this.props.active_gif === null) {
			return null; 
		}

		const style = {
			transform: this.state.is_gif ? 'translateX(0)': 'translateX(52px)',
			width: this.state.is_gif ? '51px' : '86px',
		}; 

		return (
		<div className="search-result">
			<div className="search-title-container">
				<h1 className='GIF-TITLE flex relative'>
					<div className="trending-btn">Related</div>
				</h1>
				<h1 className="GIF-TITLE flex relative">
					<div className="trending-btn" onClick={()=>{
						this.setGif(true);this.props.handleSubmit(this.props.active_title, true)}}>Gifs</div>
					<div className="favorite-btn" onClick={()=>{this.setGif(false);this.props.handleSubmit(this.props.active_title, false)}}>Stickers</div>
					<div className="animated-ellipse" style={style}></div>
				</h1>
			</div>
			<div className="search-result-container">{this.searchResultToExpandable()}</div>
		</div>
		);
	}
}

export default SearchResult;
