import React, { Component } from 'react';
import SearchBar from '../components/SearchBar'; 
import MainContent from '../components/MainContent';
import BottomScrollListener from 'react-bottom-scroll-listener';

import Logo from '../components/Logo';
import '../stylesheets/Giphy.css';

/*
* Main Page of Giphy App 
*/
class Giphy extends Component {

	constructor() {
		super();
		this.state = {
			trending_visible: true,
			active_gif: null,  
			is_gif: true,
			search_content: [],
			randoms: [],
			active_title: '',
			api_key: 'IUBMJK3PhfJzLk9G9X7hjja9Fm9fuODi',
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleSubmitAndActive = this.handleSubmitAndActive.bind(this);
		this.setMainState = this.setMainState.bind(this);
		this.handleActiveGif = this.handleActiveGif.bind(this);
		this.setRandoms = this.setRandoms.bind(this);
		this.setGif = this.setGif.bind(this);
		this.callback = this.callback.bind(this);
	}

	getRandom() {
		var randomWords = require('random-words');
		return randomWords();
	}


	setGif(boolean) {
		this.setState({
			is_gif: boolean,
		});
	}

	/*
	*  Search Endpoint with random string and rating 
	*/
	setRandoms(is_gif, is_new, offset) {
		/*
		* Selects a random word 
		*/
		let random_word = '';
		if (is_new === true) {
			random_word = this.getRandom();
		} else {
			random_word = this.state.active_title;
		}
		let path = 'https://api.giphy.com/v1/';
		// console.log('this.state.is_gif', this.state.is_gif)
		if (is_gif === true) {
			path += 'gifs';
		} else {
			path += 'stickers';
		}
		path += '/search?api_key=' + this.state.api_key;
		path += '&q=' + random_word;
		path += '&offset=' + offset; 
		// path += '&rating=' + this.state.rating;
		console.log('path (set random)', path)
	    fetch(path, {mode: 'cors'})
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				active_title: random_word,
				randoms: offset === 0 ? data.data : this.state.randoms.concat(data.data),
			});
		});
	}

	/*
	* Handle Submit for Search 
	*/
	handleSubmit(str, is_gif, offset) {
		const api_key = this.state.api_key;
		let path = 'https://api.giphy.com/v1/' 
		if (is_gif === true) {
			path += 'gifs'
		} else {
			path += 'stickers'
		}
		path += '/search?api_key=' + api_key
		path += '&q=' + str;
		path += '&offset=' + offset; 
	    fetch(path)
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				trending_visible: false,
				search_content: offset === 0 ? data.data : this.state.search_content.concat(data.data),
				active_title: str, 
			});
		})
	}


	/*
	* Handle Submit AND Active Gif for Search 
	*/
	handleSubmitAndActive(event, str, is_gif) {
		event.preventDefault();
		console.log('event.target.value', str);
		const api_key = this.state.api_key;
		let path = 'https://api.giphy.com/v1/' 
		if (is_gif === true) {
			path += 'gifs'
		} else {
			path += 'stickers'
		}
		path += '/search?api_key=' + api_key
		path += '&q=' + str;
		console.log('path (submit & active)', path)
	    fetch(path)
		.then(results =>{
			return results.json();
		}).then(data=> {
			const rand = Math.floor(Math.random() * (data.data.length -1));
			if (data.data.length === 0) {
				alert('Couldn\'t find any GIFS or Stickers');
			} else {
				this.setState({
					trending_visible: false,
					search_content: data.data,
					active_gif: data.data[rand],
					active_title: this.titleToSearchable(data.data[rand]), 
					randoms: [],
				});
			}
		})
	}

	componentWillMount() {
		this.setRandoms(true, true, 0);
	}

	/*
	* Sets active_gif in Giphy to a GIF object 
	*/	
	handleActiveGif(pic) {
		this.setState({
			active_gif: pic,
			active_title: this.titleToSearchable(pic),
			trending_visible: false,
		});
	}
	/*
	* Convert Active Gif to a searchable Title 
	*/
	titleToSearchable(pic){
		let title = pic.title.toLowerCase().trim();
		if (title === '') {
			console.log('slug')
			let slug = pic.slug.toLowerCase().trim().split('-');
			slug = slug.filter(function(str){return !(/\d/.test(str))})
			if (slug.length === 1) {
				console.log('username', pic.username);
				return pic.username;
			}
			console.log('slug',slug.join(' '));
			return slug.join(' ');
		}
		let title_arr = title.split(" "); 
		console.log('title', title_arr.slice(0, title_arr.indexOf('gif')).join(' '));
		return title_arr.slice(0, title_arr.indexOf('gif')).join(' ');
	}

	/*
	* Sets layout and content to Main-Page state 
	*/
	setMainState() {
		this.setState({
			trending_visible: true,
			main_content: [],
			active_gif: null,
			search_content: [],
		});
		this.setRandoms(true,true, 0);
	}

	callback() { 
		alert('callback!!!!')
		// if (this.state.active_gif !== null) {
		// 	console.log('implement later')
		// } else {
		this.setRandoms(this.state.is_gif === true, false, this.state.search_content.length)
		// }
	}


	render() {
		console.log('this.state.active_title', this.state.active_title)
		return (
			<div className="giphy">
				<Logo setMainState={this.setMainState}/>
		    	<SearchBar handleSubmit={this.handleSubmit} handleSubmitAndActive={this.handleSubmitAndActive}/>
		    	<MainContent setGif={this.setGif} is_gif={this.state.is_gif} setRandoms={this.setRandoms} titleToSearchable={this.titleToSearchable} randoms={this.state.randoms} api_key={this.state.api_key} active_title={this.state.active_title} handleSubmit={this.handleSubmit} dimensions={this.props.dimensions} search_content={this.state.search_content} handleActiveGif={this.handleActiveGif} active_gif={this.state.active_gif} trending_visible={this.state.trending_visible}/>
				<BottomScrollListener onBottom={this.callback} />
			</div>
		);
	}
}

export default Giphy;
