import React, { Component } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import '../stylesheets/GifRandom.css';

/*
* 
*/
class GifRandom extends Component {

	constructor() {
		super();
		this.state = {
			randoms: [],
			stickers: [],
			is_gif: true,
			rotate: 0,
		}
		this.random_word = '';
		this.setRating = this.setRating.bind(this);
	}

	getRandom() {
		var randomWords = require('random-words');
		return randomWords();
	}

	componentWillMount() {
		this.setRandoms(true, true);
	}

	/*
	*  Search Endpoint with random string and rating 
	*/
	setRandoms(is_gif, is_new) {
		/*
		* Selects a random word 
		*/
		if (is_new === true) {
			this.random_word = this.getRandom();
		}
		let path = 'https://api.giphy.com/v1/';
		// console.log('this.state.is_gif', this.state.is_gif)
		if (is_gif === true) {
			path += 'gifs';
		} else {
			path+= 'stickers';
		}
		path += '/search?api_key=' + this.props.api_key;
		path += '&q=' + this.random_word;
		path += '&rating=' + this.state.rating;
		// console.log('path', path)
	    fetch(path, {mode: 'cors'})
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				randoms: data.data,
			});
		});
	}

	renderRandomButton(){
		const className = 'rotate-'+ (this.state.rotate % 360);
		return (
			<svg className={"GIF-SVG random-svg " + className} onClick={()=>{this.setState({rotate: this.state.rotate + 90}); this.setRandoms(this.state.is_gif, true);}} viewBox="0 0 512 512" xmlns="http://www.w3.org/2000/svg">
				<path className="circle" d="m256 512c-140.609375 0-256-115.390625-256-256s115.390625-256 256-256 256 115.390625 256 256-115.390625 256-256 256zm0 0"/>
				<path className="circle" d="m512 256c0-140.609375-115.390625-256-256-256v512c140.609375 0 256-115.390625 256-256zm0 0" fill="#c6e2e7"/>
				<path className="arrow" d="m376 166-60 90h30c0 24.039062-9.359375 46.640625-26.351562 63.648438-17.007813 16.992187-39.609376 26.351562-63.648438 26.351562-14.589844 0-28.652344-3.449219-41.253906-9.960938l-33.394532 50.097657c21.984376 12.636719 47.472657 19.863281 74.648438 19.863281 41.421875 0 78.921875-16.789062 106.066406-43.933594 27.144532-27.144531 43.933594-64.644531 43.933594-106.066406h31zm0 0" />
				<path className="arrow" d="m362.066406 362.066406c27.144532-27.144531 43.933594-64.644531 43.933594-106.066406h31l-61-90-60 90h30c0 24.039062-9.359375 46.640625-26.351562 63.648438-17.007813 16.992187-39.609376 26.351562-63.648438 26.351562v60c41.421875 0 78.921875-16.789062 106.066406-43.933594zm0 0" />
				<path className="arrow" d="m256 106c-41.421875 0-78.921875 16.789062-106.066406 43.933594-27.144532 27.144531-43.933594 64.644531-43.933594 106.066406h-30l60 90 60-90h-30c0-24.039062 9.359375-46.640625 26.351562-63.648438 17.007813-16.992187 39.609376-26.351562 63.648438-26.351562 14.589844 0 28.652344 3.449219 41.253906 9.957031l33.394532-50.09375c-21.984376-12.636719-47.472657-19.863281-74.648438-19.863281zm0 0" />
				<path className="arrow" d="m297.253906 175.957031 33.394532-50.09375c-21.984376-12.636719-47.472657-19.863281-74.648438-19.863281v60c14.589844 0 28.652344 3.449219 41.253906 9.957031zm0 0" />
			</svg>
		);
	}

	randomsToExpandable(){
		return this.state.randoms.map((pic)=>{
			return (<ExpandableGif key={pic.id} is_gif={this.state.is_gif} active_title={this.props.active_title} searchResult={this.props.searchResult} handleSubmit={this.props.handleSubmit} height_is_fixed={false} handleShareButton={this.props.handleShareButton} pic={pic} handleActiveGif={this.props.handleActiveGif} handleInfoButton={this.props.handleInfoButton}/>);
		});
	}

	setGif(boolean) {
		this.setState({
			is_gif: boolean,
		});
	}

	/*
	* As of 4/27, rating parameter is broken 
	* Reimplement when Giphy fixes the issue 
	*/
	setRating(event) {
		const rating = event.target.value;
		const api_key = this.props.api_key;
		const path = 'https://api.giphy.com/v1/gifs/search?api_key=' + api_key + '&q=' + this.random_word +'&rating=' + rating;
	    fetch(path, {mode: 'cors'})
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				rating: rating,
				randoms: data.data,
			});
		})
	}

	/*
	* As of 4/27, rating parameter is broken 
	* Reimplement when Giphy fixes the issue 
	*/
	renderRatingSelect() {
		return(
			<form className="rating">
				<select className="form-control" onChange={this.setRating} value={this.state.rating}>
					<option value="G">{'G'}</option>
					<option value="PG-13">{'PG-13'}</option>
					<option value="R">{'R'}</option>
				</select>
			</form>
		);
	}

	render() {
		console.log('this.props.active_gif', this.props.active_gif);
		if (this.props.active_gif !== null) {
			return null;
		}
		// console.log(this.state.rating);
		const style = {
			transform: this.state.is_gif ? 'translateX(0)': 'translateX(52px)',
			width: this.state.is_gif ? '51px' : '86px',
		}; 
		
		return (
			<div className="gif-random">
				<div className="title-container">
					<h1 className='GIF-TITLE flex'>
						<div>{'random word'}</div>
						{this.renderRandomButton()}
						<div className='random-word'>{this.random_word}</div>
					</h1> 
					<h1 className="GIF-TITLE flex relative">
						<div className="trending-btn" onClick={()=>{this.setGif(true); this.setRandoms(true, false);}}>Gifs</div>
						<div className="favorite-btn" onClick={()=>{this.setGif(false); this.setRandoms(false, false);}}>Stickers</div>
						<div className="animated-ellipse" style={style}></div>
					</h1>
				</div>
				<div className="random-container">{this.randomsToExpandable()}</div>
			</div>
		);
	}
}

export default GifRandom;
