import React, { Component } from 'react';
import SearchBar from '../components/SearchBar'; 
import MainContent from '../components/MainContent';
import Logo from '../components/Logo';
import '../stylesheets/Giphy.css';

/*
* Main Page of Giphy App 
*/
class Giphy extends Component {

	constructor() {
		super();
		this.state = {
			trending_visible: true,
			active_gif: null,  
			search_content: [],
			randoms: [],
			active_title: '',
			api_key: 'ewnfHypctYT5Bn4nXkQpC9ebtdX9dDBG',
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.setMainState = this.setMainState.bind(this);
		this.handleActiveGif = this.handleActiveGif.bind(this);
		this.setRandoms = this.setRandoms.bind(this);
	}

	getRandom() {
		var randomWords = require('random-words');
		return randomWords();
	}

	/*
	*  Search Endpoint with random string and rating 
	*/
	setRandoms(is_gif, is_new) {
		/*
		* Selects a random word 
		*/
		let random_word = '';
		if (is_new === true) {
			random_word = this.getRandom();
		} else {
			random_word = this.state.active_title;
		}
		let path = 'https://api.giphy.com/v1/';
		// console.log('this.state.is_gif', this.state.is_gif)
		if (is_gif === true) {
			path += 'gifs';
		} else {
			path += 'stickers';
		}
		path += '/search?api_key=' + this.state.api_key;
		path += '&q=' + random_word;
		// path += '&rating=' + this.state.rating;
		console.log('pah', path)
	    fetch(path, {mode: 'cors'})
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				active_title: random_word,
				randoms: data.data,
			});
		});
	}

	/*
	* Handle Submit for Search 
	*/
	handleSubmit(str, is_gif) {
		const api_key = this.state.api_key;
		let path = 'https://api.giphy.com/v1/' 
		if (is_gif === true) {
			path += 'gifs'
		} else {
			path += 'stickers'
		}
		path += '/search?api_key=' + api_key + '&q=';
	    fetch(path + str)
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				trending_visible: false,
				search_content: data.data,
			});
		})
	}

	componentWillMount() {
		this.setRandoms(true, true);
	}

	/*
	* Sets active_gif in Giphy to a GIF object 
	*/	
	handleActiveGif(pic) {
		this.setState({
			active_gif: pic,
			active_title: this.titleToSearchable(pic),
			trending_visible: false,
		});
	}
	/*
	* Convert Active Gif to a searchable Title 
	*/
	titleToSearchable(pic){
		let title = pic.title.toLowerCase().trim();
		if (title === '') {
			console.log('slug')
			let slug = pic.slug.toLowerCase().trim().split('-');
			slug = slug.filter(function(str){return !(/\d/.test(str))})
			if (slug.length === 1) {
				console.log('username', pic.username);
				return pic.username;
			}
			console.log('slug',slug.join(' '));
			return slug.join(' ');
		}
		let title_arr = title.split(" "); 
		console.log('title', title_arr.slice(0, title_arr.indexOf('gif')).join(' '));
		return title_arr.slice(0, title_arr.indexOf('gif')).join(' ');
	}

	/*
	* Sets layout and content to Main-Page state 
	*/
	setMainState() {
		this.setState({
			trending_visible: true,
			main_content: [],
			active_gif: null,
			search_content: [],
		});
		this.setRandoms(true,true);
	}

	render() {
		console.log('this.state.active_title', this.state.active_title)
		return (
			<div className="giphy">
				<Logo setMainState={this.setMainState}/>
		    	<SearchBar handleSubmit={this.handleSubmit}/>
		    	<MainContent setRandoms={this.setRandoms} titleToSearchable={this.titleToSearchable} randoms={this.state.randoms} api_key={this.state.api_key} active_title={this.state.active_title} handleSubmit={this.handleSubmit} dimensions={this.props.dimensions} search_content={this.state.search_content} handleActiveGif={this.handleActiveGif} active_gif={this.state.active_gif} trending_visible={this.state.trending_visible}/>
			</div>
		);
	}
}

export default Giphy;
