import React, { Component } from 'react';
import ExpandableGif from '../components/ExpandableGif';
import '../stylesheets/GifRandom.css';

/*
* 
*/
class GifRandom extends Component {

	constructor() {
		super();
		this.state = {
			randoms: [],
			rating: 'g',
			maxRandom: 10,
		}
	}

	/*
	*  Random Gifs according to Rating 
	*/
