import React, { Component } from 'react';
import SearchBar from '../components/SearchBar'; 
import MainContent from '../components/MainContent';
import '../stylesheets/Giphy.css';

/*
* Main Page of Giphy App 
*/
class Giphy extends Component {

	constructor() {
		super();
		this.state = {
			horizontal_trending: true,
			active_gif: null,  
			main_content: [],
			sad_gif: null, 
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.setMainState = this.setMainState.bind(this);
		this.handleActiveGif = this.handleActiveGif.bind(this);
	}

	/*
	* Handle Submit for Search 
	*/
	handleSubmit(event) {
		const path = 'https://api.giphy.com/v1/gifs/search?api_key=20RpuHkJdS4eOEQg5OOBjQNSqLAnzgmP&q=';
	    event.preventDefault();
	    fetch(path + event.target.value)
		.then(results =>{
			return results.json();
		}).then(data=> {
			this.setState({
				horizontal_trending: false,
				main_content: data.data,
			});
		})
	}

	/*
	* Sets active_gif in Giphy to a GIF object 
	*/	
	handleActiveGif(pic) {
		this.setState({
			active_gif: pic,
			horizontal_trending: false,
		});
	}

	/*
	* Sets layout and content to Main-Page state 
	*/
	setMainState() {
		this.setState({
			horizontal_trending: true,
			main_content: [],
			active_gif: null,
		});
	}

	/*
	* Logo 
	* Credit: Giphy 
	*/	
	logo() {
		return (
			<svg className="logo" onClick={this.setMainState} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 164 35" itemProp="logo">
				<g clipRule="evenodd">
					<path className="left-line" d="M0 3h4v29H0z"></path>
					<path className="right-line" d="M24 11h4v21h-4z"></path>
					<path className="bottom-line" d="M0 31h28v4H0z"></path>
					<path className="top-line" d="M0 0h16v4H0z"></path>
					<path className="zig-zag-line" d="M24 8V4h-4V0h-4v12h12V8"></path>
					<path className="zig-zag-line-2" d="M24 16v-4h4M16 0v4h-4"></path>
				</g>
				<g>
					<path className="logo-text" d="M59.1 12c-2-1.9-4.4-2.4-6.2-2.4-4.4 0-7.3 2.6-7.3 8 0 3.5 1.8 7.8 7.3 7.8 1.4 0 3.7-.3 5.2-1.4v-3.5h-6.9v-6h13.3v12.1c-1.7 3.5-6.4 5.3-11.7 5.3-10.7 0-14.8-7.2-14.8-14.3 0-7.1 4.7-14.4 14.9-14.4 3.8 0 7.1.8 10.7 4.4L59.1 12zM68.2 31.2V4h7.6v27.2h-7.6zM88.3 23.8v7.3h-7.7V4h13.2c7.3 0 10.9 4.6 10.9 9.9 0 5.6-3.6 9.9-10.9 9.9h-5.5zm0-6.5h5.5c2.1 0 3.2-1.6 3.2-3.3 0-1.8-1.1-3.4-3.2-3.4h-5.5v6.7zM125 31.2V20.9h-9.8v10.3h-7.7V4h7.7v10.3h9.8V4h7.6v27.2H125zM149.2 13.3l5.9-9.3h8.7v.3l-10.8 16v10.8h-7.7V20.3L135 4.3V4h8.7l5.5 9.3z"></path>
				</g>
			</svg>
		);
	}

	render() {
		return (
			<div className="giphy">
				{this.logo()}
		    	<SearchBar handleSubmit={this.handleSubmit}/>
		    	<MainContent sad_gif={this.state.sad_gif} dimensions={this.props.dimensions} main_content={this.state.main_content} handleActiveGif={this.handleActiveGif} active_gif={this.state.active_gif} horizontal_trending={this.state.horizontal_trending}/>
			</div>
		);
	}
}

export default Giphy;
